import React from 'react';
import { useEffect } from "react";
import { useNavigate, Link } from 'react-router-dom';
import ChkAuth from './chkAuth';
import Google from '../images/icons/icon-google.png'
import SendData from './SendData';
import ConfirmSmsSignIn from './ConfirmSmsSignIn';
import ConfirmEmailSignIn from './ConfirmEmailSignIn';
import ResetRequest from './UpdateDetails';
import ResetPassword from './ResetPassword';
import RequestOTP from './RequestOTP';
import { useAlert } from "react-alert";
import UpdateHybrid from './UpdateHybrid';

export default function RenderSignin () {

	const Navigate = useNavigate();
	const [stage, setStage] = React.useState("stage1");
	const [username, setUsername] = React.useState("");
	const [password, setPassword] = React.useState("");
	const [address, setAddress] = React.useState("")
	const [email, setEmail] = React.useState("");
	const [mobile, setMobile] = React.useState("");
	const [pagetype, setPageType] = React.useState("init");
	const alert = useAlert()

	useEffect( () => { //check if signed in and go to profile page
		ChkAuth().then(user => {
		  if (user) {
		  Navigate("/profile/" + user + "/home")
		  } 
		})
	},[]);


	async function signin(){ // Sign in, check password, get token

		const username = document.getElementById('username').value;
		const password = document.getElementById('pass').value;

		let signindata = {data: {
			Username: username,
			Password: password,
			RequestType: "stage1"
			}
		}

		let gqlRequest = "mutation SignIn ($data: SecurityCheckInput!){ SignIn(input: $data) { Token } }"

		let response = await SendData(gqlRequest, signindata, 'signin')


		if ( "errors" in response ){ // if password is a match redirect to profile page
			console.log("Unable to sign in", response.errors[0].message )
			alertError(response.errors[0].message)

		} else { // if password is a match redirect to profile page
			alert.show("Please enter OTP sent to your mobile")
			setUsername(username)
			setPassword(password)
			setStage("stage2")

		} 


	} 

	async function GetOTP (requestType, pagetype) {

		if (requestType == "none") {
			setAuthType(requestType)
			setPageType(pagetype)
			return
		}

		let username = "undefined"
		let password = "undefined"
		if (document.getElementById('username').value) {
			username = document.getElementById('username').value
			password = document.getElementById('pass').value
			setUsername(username)
			setPassword(password)
			RequestOTP(username, requestType).then((response) => {
				if (( "errors" in response )) {
					console.log("error bak from otp request", response.errors[0].message)
					alertError(response.errors[0].message)
				} else {
					setAddress( response.data.SecureUpdate.Token.split(":"))
					setAuthType(requestType)
					setPageType(pagetype)

				}
			})


		} else {
			alert.show("Please enter your username")
		}
	}

	function StartReset () {
		setStage("forgot")
	}


	function alertError(error){
		const delimiter = '= '
		const start = 2
		const tokens = error.split(delimiter).slice(start)
		const result = tokens.join(delimiter); // those.that
		alert.show(result)
	}

  return (  
    <>
	{stage === "email" && <ConfirmEmailSignIn username={username} password={password} address={address} />}
	{stage === "sms" && <ConfirmSmsSignIn username={username} password={password} address={address}/>}
	{stage === "forgot" && <UpdateHybrid username={username}  address ={address} updatetype = {"Password"} authtype ={"both"} />}

	{stage === "stage1" && 
		<>
		<span className="login100-form-title p-b-53">
			Sign In With
		</span>
		<a href="/#" className="btn-face m-b-20">
			<i className="fa fa-facebook-official"></i>
			Facebook
		</a>
		<a href="/#" className="btn-google m-b-20">
			<img src={Google} alt="GOOGLE" />
			Google
		</a>
		<div className="p-t-31 p-b-9">
			<span className="txt1">
				Username
			</span>
		</div>
		<div className="wrap-input100 validate-input" data-validate = "Username is required">
			<input className="input100" type="text" name="username" id="username"/>
			<span className="focus-input100"></span>
		</div>
		<div className="p-t-13 p-b-9">
			<span className="txt1">
				Password
			</span>
			{/* <a href="/" className="txt2 bo1 m-l-5">
				Forgot?
			</a> */}
		</div>
		<div className="wrap-input100 validate-input" data-validate = "Password is required">
			<input className="input100" type="password" name="pas" id="pass" />
			<span className="focus-input100"></span>
		</div>
		<div className="container-login100-form-btn m-t-17">
			<button className="login100-form-btn" type="button" onClick={() => GetOTP("email")}>
				Auth with email
			</button>
			<button className="login100-form-btn" type="button" onClick={() => GetOTP("sms")}>
				Auth with sms
			</button>
		</div>
		<div className="w-full text-center p-t-55">
			<span className="txt2" style={{marginRight:"10px", color:"black"}}>
				Not a member?
			</span>
			<Link to="/signUp">Sign up now</Link>
			<button className="login100-form-btn" type="button" onClick={() => G()}>
				Forgot Password
			</button>
		</div>

		</>
	}
    </>
  )
};
