import React from 'react';
import { useEffect } from "react";
import { useNavigate, Link } from 'react-router-dom';
import ChkAuth from './chkAuth';
import Google from '../images/icons/icon-google.png'
import SendData from './SendData';
import ResetPassword from './ResetPassword';
import UpdateUsername from './UpdateUsername';
import UpdateEmail from './UpdateEmail';
import UpdateMobile from './UpdateMobile';
import { useAlert } from "react-alert";

export default function RenderResetRequest (props) {

	const Navigate = useNavigate();
	const [updateType, setUpdateType] = React.useState("init");
	const [username, setUsername] = React.useState(props.username)
	const [password, setPassword] = React.useState(props.password)
	const [exte, setInternal ] = React.useState("false")

	const [address, setAddress] = React.useState("unassigned")

	useEffect( () => { //check if signed in and go to profile page
		ChkAuth().then(user => {
		  if (user) {
		  Navigate("/profile/" + user + "/home")
		  } 
		})
	},[]);

	console.log("resert request username: Password:", username, password)




	async function RequestOTP (requesttype) {


		const otpType = {
			"Mobile": "email",
			"Email": "sms",
			"Password": "both",
			"Username": "both"

		}

		console.log("Requesting OTP", requesttype, otpType[requesttype])

		let signindata = {data: {
			Username: username,
			RequestType: otpType[requesttype],
			UpdateType: "user"
			}
		}

		let gqlRequest = "mutation SecureUpdate ($data: SecurityCheckInput!){ SecureUpdate(input: $data) { Token } }"

		let response = await SendData(gqlRequest, signindata, 'secureupdate')


		if ( "errors" in response ){ // if password is a match redirect to profile page
			console.log("Unable to send OTP", response.errors[0].message )
			alertError(response.errors[0].message)

		} else { // if password is a match redirect to profile page
			setAddress(response.data.SecureUpdate.Token)
			setUpdateType(requesttype)

		} 

	}



	function alertError(error){
		const delimiter = '= '
		const start = 2,
		tokens = error.split(delimiter).slice(start),
		result = tokens.join(delimiter); // those.that
		alert.show(result)
	}

	function LandingPage ()	{
		switch (updateType) { 
			case "Password":
				return <ResetPassword username={username} password={password} address ={address} internal = {internal}/>
			break
			case "Username":
				return <UpdateUsername username={username} password={password} address ={address}/>
			break
			case "Email":
				return <UpdateEmail username={username} password={password} address ={address}/>
			break
			case "Mobile":
				return <UpdateMobile username={username} password={password} address ={address} internal = {internal}/>
			break
			default:
				return (
					<>
						<span className="login100-form-title p-b-53">
						<h3><i class="fa fa-lock fa-4x"></i></h3>
						</span>
						<select  onChange={event => RequestOTP(event.target.value)}>
							<option value = "Select">Select</option>
							<option value="Mobile" >Mobile</option>
							<option value="Email">Email</option>
							<option value="Username">Username</option>
							<option value="Password">Password</option>
						</select>
						<div className="w-full text-center p-t-55">
							<button onClick={() => window.location.reload(false)}>Back to log in</button>
						</div>
					</>
				)

		}}

  return (  
    <>
		<LandingPage/>
	</>


  )
};
