import React from 'react';
import { useNavigate, useEffect, Link } from 'react-router-dom';
import SendData from './SendData';
import ConfirmEmail from './ConfirmEmail';

export default function ConfirmSms (temp_username, temp_password, temp_email, temp_mobile) {
	
	//const Navigate = useNavigate();
	const [stage, setStage] = React.useState("stage2");
	const [username, setUsername] = React.useState(username);
	const [password, setPassword] = React.useState(password);
	const [email, setEmail] = React.useState(email);
	const [mobile, setMobile] = React.useState(	mobile);
	const [otp_sms, setOtp_sms] = React.useState(	"");

	async function verify(){
		
		const otp = document.getElementById('mobileotp').value;
			
		let signindata = {data:{
			Username: temp_username,
			Password: temp_password,
			Email:  temp_email,
			Mobile: temp_mobile,
			OTP_Mobile: otp,
			RequestType: stage
			}
		}

		let gqlRequest = "mutation SignUp ($data: SecurityCheckInput!){ SignUp(input: $data) { Token } }"
		
		let response = await SendData(gqlRequest, signindata, 'signup')

		
		if ( response.data.SignUp.Token == "proceed" ){ // if password is a match redirect to profile page
			alert("Please confirm your email")
			setUsername(temp_username)
			setPassword(temp_password)
			setEmail(temp_email)
			setMobile(temp_mobile)
			setOTP_Mobile(otp)
			setStage("stage3")
		} 
		
		
	}

	return ( 
		<>
		{stage === "stage3" ?

      		<ConfirmEmail username={username} password={password} email={email} mobile={mobile} />
			:
			<>
				<span className="login100-form-title p-b-53">
				<h3><i class="fa fa-lock fa-4x"></i></h3>
				</span>
				<div>
					
				</div>
				
				<div className="p-t-31 p-b-9">
					<span className="txt1">
						Enter the code sent to {mobile}
					</span>
				</div>
				<div className="wrap-input100 validate-input" data-validate = "verification code is required">
					<input className="input100" type="text" name="username" id="username"/>
					<span className="focus-input100"></span>
				</div>
				<div >
					<Link to="/reset">Resend code</Link>
				</div>
				<div className="container-login100-form-btn m-t-17">
				</div>
				<div className="container-login100-form-btn m-t-17">
					<button className="login100-form-btn" type="button" onClick={verify}>
						Authenticate
					</button>
				</div>
				<div className="w-full text-center p-t-55">
					<Link to="/signIn">Back to log in</Link>
				</div>
				
			</>
		}
		</> 
	)
};
