import React from 'react';
import { useNavigate, useEffect, Link } from 'react-router-dom';
import SendData from './SendData';
import { useAlert } from "react-alert";

export default function ResetPassword (props) {
	
	const Navigate = useNavigate();
	const [stage, setStage] = React.useState("stage2");
	const [username, setUsername] = React.useState(props.username);
	const [password, setPassword] = React.useState(props.password);
	const [otp_sms, setOtp_sms] = React.useState("");
	const alert = useAlert()

	

	
	function alertError(error){
		const delimiter = '= '
		const start = 2,
		tokens = error.split(delimiter).slice(start),
		result = tokens.join(delimiter); // those.that
		alert.show(result)
	}

	return ( 
		<>
		<span className="login100-form-title p-b-53">
			  Confirm your details
		  </span>
		  <div className="p-t-31 p-b-9">
			  <span className="txt1">
				  Username
			  </span>
		  </div>
		  <div className="wrap-input100 validate-input" data-validate = "Username is required">
			  <input className="input100" type="text" name="username" id="username"/>
			  <span className="focus-input100"></span>
		  </div>
		  <div className="container-login100-form-btn m-t-17">
			  <button className="login100-form-btn" type="button" onClick={resetpassword}>
				  Send verification sms
			  </button>
		  </div>
		  <div className="w-full text-center p-t-55">
			  <Link to="/signIn">Back to log in</Link>
		  </div>
	  </>
		
)
};
