import React from 'react';
import { useNavigate, useEffect, Link } from 'react-router-dom';
import SendData from './SendData';
import { useAlert } from "react-alert";
import RequestOTP from './RequestOTP';
import Home from './Home';

export default function SignUpHybrid (props) {

	const Navigate = useNavigate();
	const [page, setPage] = React.useState("default");
	const [stage, setStage] = React.useState("stage2");
	const [username, setUsername] = React.useState(props.username);
	const [rendertype, setRenderType] = React.useState("email"); // change when stage 1 backend responds with first step and stage 2 next step to allow any mix /order of auths and protect user
	const [authtype, setAuthType] = React.useState(props.authtype);
	const [password, setPassword] = React.useState(props.password);
	const [email, setEmail] = React.useState(props.email);
	const [mobile, setMobile] = React.useState(props.mobile);
	const [address, setAddress] = React.useState(props.address);
	const [emailotp, setEmailOtp] = React.useState("");
	const [userdata, setUserdata] = React.useState({
		Username: props.username,
		Password: props.password,
		Email: props.email,
		Mobile: props.mobile,
		RequestType: "stage2",
	});

	//const [updatetype, setUpdateType] = React.useState(props.updatetype);
	const alert = useAlert()

	async function signupHybrid(){


		console.log("signupHybrid rendered:", username, "password:", password, "rendertype: ", rendertype, "email:", email, "mobile:", mobile, "address:", address, "authtype:", authtype, "stage:", stage, "emailotp:", emailotp)
		//const emailotp = document.getElementById('resetmobileotp').value;

		let signupdata = userdata // copy to avoid state not updating in time



		// let getValues = []
		// authtype === "both" ? getValues = ["sms", "email"] : authtype === "none" ? getValues = ["password"] : getValues = [authtype]

		//console.log("getValues", getValues)
		if (rendertype === "sms") {
			const sms = document.getElementById('sms').value;
			signupdata.OTP_Mobile = sms
		}
		if (rendertype === "email") {
			const email = document.getElementById('email').value;
			signupdata.OTP_Email = email
			//setEmailOtp(email)
		}

		if (rendertype === "oauth") {
			const passcode = document.getElementById('oauth').value;
			signupdata.Oauth = passcode
		}

		// if (stage === "stage3") {
		// 	signupdata.data.OTP_Email = emailotp
		// }

		console.log("sending data to graphql", signupdata)
		let gqlRequest = "mutation SignUp ($data: SecurityCheckInput!){ SignUp(input: $data) { Token } }"

		let response = await SendData(gqlRequest, {data:signupdata}, 'signup')


		if ( "errors" in response ){ // if password is a match redirect to profile page
			//{ProcessErrorAlerts("hi", "hi")}
			console.log("please try again", response.errors[0].message )

		} else { // if password is a match redirect to profile page
			if (response.data.SignUp.Token === "proceed") {
				//stage === "stage2" && setEmailOtp(signupdata.data.OTP_Email)
				setUserdata(signupdata);
				setStage("stage3")
				console.log("setting rendertype to", authtype, "and stage to", stage)
				setRenderType(authtype)//update auth from default email everyone gets 


			} else {
					localStorage.setItem('jwt_token', response.data.SignUp.Token) // Store JWT in storage
				   setPage("home")
			   }
			}
		} 




	// async function ResendOTP (requestType) {

	// 	// let signupdata = {data:{
	// 	// 	Username: username,
	// 	// 	RequestType: "stage1",
	// 	// 	UpdateData: "resend"
	// 	// 	}
	// 	// }

	// 	// let gqlRequest = "mutation SignUp ($data: SecurityCheckInput!){ SignUp(input: $data) { Token } }"

	// 	// let response = await SendData(gqlRequest, signupdata, 'signup')


	// 	// if ( "errors" in response ){ // if password is a match redirect to profile page
	// 	// 	//{ProcessErrorAlerts("hi", "hi")}
	// 	// 	console.log("please try again", response.errors[0].message )

	// 	// } else { // if password is a match redirect to profile page
	// 	// 	alert.show("OTP resent")
	// 	// }
	// }

	async function ResendOTP (requestType) {
		RequestOTP(username, requestType, "temp").then((response) => {
			if (( "errors" in response )) {
				console.log("error bak from otp request", response.errors[0].message)
				alertError(response.errors[0].message)
			}
		})
	}


	function alertError(error){
		const delimiter = '= '
		const start = 2,
		tokens = error.split(delimiter).slice(start),
		result = tokens.join(delimiter); // those.that
		alert.show(result)
	}

	function ChangeAuthType () {

		console.log("change auth type called", setRenderType)
		//fun is only called when type is sms as button only shown on that page. added switch from email for future
		if (rendertype === "sms") {
			ResendOTP("!email")
			setRenderType("email")

		} else {
			ResendOTP("!sms")
			setRenderType("sms")

		}
	}



	function ConfirmSms () {
		return (
			<>
				<div className="p-t-31 p-b-9">
					<span className="txt1">
						{"Enter code sent to *******" + address[0]}
					</span>
				</div>
				<div className="wrap-input100 validate-input" data-validate = "verification code is required">
					<input className="input100" type="text" name="mobileotp" id="sms"/>
					<span className="focus-input100"></span>
				</div>
				<div >
					<button  type="button" onClick={() => ResendOTP("!sms")}>
						resend code
					</button>
				</div>
				<div >
					<button  type="button" onClick={() => ChangeAuthType()}>
						dont have acccess to phone
					</button>
				</div>
				<div className="container-login100-form-btn m-t-17">
				</div>
			</>
		)
	}

	function ConfirmEmail () {
		return (
			<>
				<div className="p-t-31 p-b-9">
					<span className="txt1">
						{"Enter code sent to email " + address[1] + "*******"}
					</span>
				</div>
				<div className="wrap-input100 validate-input" data-validate = "verification code is required">
					<input className="input100" type="text" name="emailotp" id="email"/>
					<span className="focus-input100"></span>
				</div>
				<div >
					<button  type="button" onClick={() => ResendOTP("!email")}>
						resend code
					</button>
				</div>
				<div >
					<button  type="button" onClick={() => ChangeAuthType()}>
						dont have acccess to email
					</button>
				</div>

			</>
		)
	}

	function ConfirmPassword () {
		return (
			<>
				<div className="p-t-31 p-b-9">
					<span className="txt1">
						Enter your password
					</span>
				</div>
				<div className="wrap-input100 validate-input" data-validate = "password is required">
					<input className="input100" type="text" name="pass" id="password"/>
					<span className="focus-input100"></span>
				</div>

			</>
		)
	}

	function Verify () {
		return (
			<>

				<div className="container-login100-form-btn m-t-17">
				</div>
				<div className="container-login100-form-btn m-t-17">
					<button className="login100-form-btn" type="button" onClick={() =>signupHybrid()}>
						verify
					</button>
				</div>
				<div className="w-full text-center p-t-55">
					<button onClick={() => setPage("signup")}>Back to account details</button>
				</div>
			</>
		)
	}

	function Header () {
		return (
			<>
				<span className="login100-form-title p-b-53">
					<h3><i class="fa fa-lock fa-4x"></i></h3>
				</span>
				<div>
				</div>
			</>
		)
	}





	// function HighSecurity () {
	// 	return (
	// 		<>
	// 			<ConfirmSms/>
	// 			<ConfirmEmail/>
	// 		</>
	// 	)
	// }

	const authTypeMap = {
		"email": <ConfirmEmail/>,
		"sms": <ConfirmSms/>,
	//	"both": <HighSecurity/>,
		"none": <ConfirmPassword/>
	}


	function Confirm () {
		return (
			<>
				<Header/>
				{authTypeMap[rendertype]}
				<Verify/>
			</>
		)
	}

	return ( 
		<>
			{page === "default" && <Confirm /> }
			{page === "home" && <Home sessionuser={username} page={"home"} viewing={username} />}

		</>

	)
