import React from 'react';
import { useNavigate, useEffect, Link } from 'react-router-dom';
import SendData from './SendData';
import SignUpHybrid from './SignUpHybrid';
import { useAlert } from "react-alert";


export default function RenderSignUp () {

  const Navigate = useNavigate();
  const alert = useAlert()
  const [page, setPage] = React.useState("default");
  const [rendertype, setRenderType] = React.useState("init"); 
  const [username, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [mobile, setMobile] = React.useState("");
  const [authtype, setAuthType] = React.useState("");
  const [address, setaddress] = React.useState("");
  const [userdata, setUserData] = React.useState({
    Username: "",
    Password: "",
    RequestType: "stage1",
  });

  async function SignUp () { // sends username, password, email from input, backend then creates s3 bucket and stores details on mongodb

    // const temp_username = document.getElementById('signupusername').value;
    // const temp_password = document.getElementById('signuppass').value;
    // const temp_email = document.getElementById('signupemail').value;
    // const temp_mobile = document.getElementById('signupmobile').value;
    // const temp_authtype = document.getElementById('authtype').value;

    let signupdata = userdata

    if (rendertype === "username") {
			const username = document.getElementById('username').value;
      const password = document.getElementById('password').value;
      signupdata.Username = username
      signupdata.Password = password
		}
		if (rendertype === "email") {
			const email = document.getElementById('email').value;
			signupdata.Email = email
			//setEmailOtp(email)
		}

    if (rendertype === "sms") {
			const email = document.getElementById('email').value;
			signupdata.Email = email
			//setEmailOtp(email)
		}

		if (rendertype === "oauth") {
			const passcode = document.getElementById('oauth').value;
			signupdata.Oauth = passcode
		}

    if (rendertype === "authtype") {
      const authtype = document.getElementById('authtype').value;
      signupdata.Token = authtype
    }

		let gqlRequest = "mutation SignUp ($data: SecurityCheckInput!){ SignUp(input: $data) { Token AuthType MobClue EmailClue } }"

		let response = await SendData(gqlRequest, {data:signupdata}, 'signup')

		if ( "errors" in response ){ // if password is a match redirect to profile page
      console.log("error procceeding with sign up", response.errors[0].message )
      alertError(response.errors[0].message)


    } else if ( response.data.SignUp.Token == "proceed" ){ // if password is a match redirect to profile page
      //alert.show("Please enter OTP sent to your email")
      //setUsername(temp_username)
      //setEmail(temp_email)
      //setMobile(temp_mobile)
      //setAuthType(temp_authtype)
      setaddress([response.data.SignUp.MobClue, response.data.SignUp.EmailClue])
      setUserData(signupdata)
      setRenderType(response.data.SignUp.AuthType)
		} 
  }

  function alertError(error){
		const delimiter = '= '
		const start = 2
		const tokens = error.split(delimiter).slice(start)
		const result = tokens.join(delimiter); // those.that
		alert.show(result)
	}

  function SignIn () {
    setPage("signin")
  }

  function CheckUsername () {
		return (
			<>
				<div className="p-t-31 p-b-9">
					<span className="txt1">
						Choose your username
					</span>
				</div>
				<div className="wrap-input100 validate-input" data-validate = "username is required">
					<input className="input100" type="text" name="pass" id="username"/>
					<span className="focus-input100"></span>
				</div>
				<button className="login100-form-btn" type="button" onClick={SignUp}>
						Verify
				</button>


			</>
		)
	}

  function LandingPage () {

    return (
      <>

          <div className="p-t-13 p-b-9">
        <span className="txt1">
          Mobile
        </span>
      </div>
      <div className="wrap-input100 validate-input" data-validate = "Mobile no. is required">
        <input className="input100" type="mobile" name="pass" id="signupmobile" />
        <span className="focus-input100"></span>
      </div>
      <div className="p-t-13 p-b-9">
        <span className="txt1">
          Email
        </span>
      </div>
      <div className="wrap-input100 validate-input" data-validate = "authtype is required">
        <input className="input100" type="email" name="pass" id= "signupemail" />
        <span className="focus-input100"></span>
      </div>
      <div className="p-t-13 p-b-9">
        <span className="txt1">
          auth type
        </span>
      </div>
      <div className="wrap-input100 validate-input" data-validate = "Email is required">
        <input className="input100" type="email" name="pass" id="authtype" />
        <span className="focus-input100"></span>
      </div>

      <div className="container-login100-form-btn m-t-17">
        <button className="login100-form-btn" type="button" onClick={() => SignUp()} >
          Sign Up
        </button>
      </div>

      <div className="w-full text-center p-t-55">
        <span className="txt2">
          Already a member? 
        </span>

        <Link to="/signIn">Sign in now</Link> 
      </div>
    </>
    )
  }

  return (
    <>
    {page === "default" && <LandingPage />}
    {page === "confirm" && <SignUpHybrid username={username}  email={email} mobile={mobile} authtype={authtype} address={address} />}
    {page === "signin" && <SignIn/>}


    </> 
  );



}
